using System.Linq;
using Rock.Data;

namespace Rock.Model
{
    /// <summary>
    /// PersonalLinkSectionOrder Service class
    /// </summary>
    public partial class PersonalLinkSectionOrderService : Service<PersonalLinkSectionOrder>
    {
        /// <summary>
        /// Updates the <see cref="IModel.ModifiedDateTime "/> of <see cref="PersonalLinkSectionOrder"/> records.
        /// Use this when a link or section has been deleted so that we know that the list has changed.
        /// </summary>
        /// <param name="preSaveChangesPersonAliasId">The pre save changes person alias identifier.</param>
        internal void UpdateLinkOrdersModifiedDateTime( int? preSaveChangesPersonAliasId )
        {
            var personSectionOrdersQuery = this.Queryable();

            // If this is a non-shared section, we only have to update the link orders for the person.
            // Otherwise, we'll update all the link orders.
            if ( preSaveChangesPersonAliasId.HasValue )
            {
                personSectionOrdersQuery = personSectionOrdersQuery.Where( a => a.PersonAliasId == preSaveChangesPersonAliasId );
            }

            this.Context.BulkUpdate( personSectionOrdersQuery, s => new PersonalLinkSectionOrder { ModifiedDateTime = RockDateTime.Now } );
        }
    }
}
