Declare @ValueTable table(SeriesName varchar(250), year int, YValue int) -- Previous Years Insert Into @ValueTable Select Concat(Yr-1,' - ',Yr), Yr, Count(A.Id) As YValue From ( Select p.Id, CASE When (Month(av.[ValueAsDateTime]) < 9) Then Year(av.[ValueAsDateTime]) Else Year(DateAdd(year,1,av.[ValueAsDateTime])) End As Yr From Person p Join AttributeValue av on av.AttributeId = 174 And p.Id = av.EntityId) As A -- Change this Inner Join ( Select MIN(g.CampusId) As CampusId, p2.Id From Person p2 Inner Join GroupMember gm on gm.PersonId = p2.Id Inner Join [Group] g on g.Id = gm.GroupId And g.GroupTypeId = 10 Group By p2.Id ) As B on A.Id = B.Id Group By Yr Select * From @ValueTable Where Year >= 2000 Order By Year